json_obj = json.loads(json_str)

# 递归函数，用于将嵌套的JSON对象转换为表格数据
def json_to_table(json_obj, parent=None, key_prefix=''):
    # 存储表格数据的列表
    rows = []
    # 遍历JSON对象的每个键值对
    for key, value in json_obj.items():
        # 生成新的键前缀
        new_key_prefix = key_prefix + '.' + key if key_prefix else key
        # 如果值是字典，则递归处理
        if isinstance(value, dict):
            rows.append({'Field': key, 'Parent': parent})
            rows.extend(json_to_table(value, key, new_key_prefix))
        # 如果值不是字典，则添加到表格数据中
        else:
            rows.append({'Field': new_key_prefix, 'Parent': parent})
    return rows

# 将Python对象转换为表格数据
data = json_to_table(json_obj)

# 将表格数据转换为pandas DataFrame
df = pd.DataFrame(data)
